import React from "react";
import "../styles/components/admin.scss";

const Admin = () => {
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (e.dataTransfer.items) {
      const files = [];
      for (let i = 0; i < e.dataTransfer.items.length; i++) {
        if (e.dataTransfer.items[i].kind === "file") {
          files.push(e.dataTransfer.items[i].getAsFile());
        }
      }
      console.log("Dropped Files:", files);
    } else {
      console.log("No valid files detected.");
    }
  };

  return (
    <div className='admin-container d-flex-full d-flex-col'>
      <div className='admin-inner-container d-flex-col gap-16 d-flex-jsc'>
        <form>
          <input type='file' name='file1' />
          <input type='file' name='file2' />
          <input type='text' name='textInput' />
        </form>
      </div>

      {/* Drag and Drop Container */}
      <div
        className='drag-container'
        onDragOver={(e) => {
          e.preventDefault();
          e.stopPropagation();
        }}
        onDragEnter={(e) => {
          e.preventDefault();
          e.stopPropagation();
        }}
        onDrop={handleDrop}
        onClick={(e) => e.preventDefault()} // Prevent unexpected navigation
        style={{
          width: "500px",
          height: "500px",
          backgroundColor: "#000",
          color: "#fff",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          border: "2px dashed white",
        }}
      >
        Drag & Drop Files Here
      </div>
    </div>
  );
};

export default Admin;
