import React from "react";
import "../styles/components/admin.scss";

const Admin = () => {
  return (
    <div className='admin-container d-flex-full d-flex-col'>
      <div className='admin-inner-container d-flex-col gap-16 d-flex-jsc'>
        <form>
          <input type='file' name='file1' />
          <input type='file' name='file2' />
          <input type='text' name='textInput' />
        </form>
      </div>

      {/* Drag and Drop Container */}
      <div
        className='drag-container'
        onDragOver={(e) => {
          e.preventDefault(); // Prevent default to allow drop
        }}
        onDrop={(e) => {
          e.preventDefault(); // Prevent default behavior
          const files = e.dataTransfer.files; // Get dropped files

          if (files.length > 0) {
            console.log("Dropped Files:", files);
          } else {
            console.log("No files detected");
          }
        }}
        style={{
          width: "500px",
          height: "500px",
          backgroundColor: "#000",
          color: "#fff",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        Drag & Drop Files Here
      </div>
    </div>
  );
};

export default Admin;
