import React, { useCallback } from "react";
import { useDropzone } from "react-dropzone";
import "../styles/components/admin.scss";

const Admin = () => {
  const onDrop = useCallback((acceptedFiles) => {
    console.log("Dropped Files:", acceptedFiles);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    multiple: true, // Allows multiple file uploads
    noClick: false, // Allows clicking to upload
    noKeyboard: true, // Disables keyboard-based file selection
  });

  return (
    <div className='admin-container d-flex-full d-flex-col'>
      <div
        className='drag-container'
        {...getRootProps()}
        style={{
          width: "500px",
          height: "500px",
          backgroundColor: isDragActive ? "#444" : "#000",
          color: "#fff",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          border: "2px dashed white",
          cursor: "pointer",
        }}
      >
        <input {...getInputProps()} />
        {isDragActive ? (
          <p>Drop files here...</p>
        ) : (
          <p>Drag & Drop files here or click to upload</p>
        )}
      </div>
    </div>
  );
};

export default Admin;
