import React, { useState } from "react";
import "../styles/components/admin.scss";

const Admin = () => {
  const [droppedFiles, setDroppedFiles] = useState([]);

  const handleDrop = (e) => {
    e.preventDefault(); // Prevent default behavior (opening files in a new tab)

    const files = Array.from(e.dataTransfer.files); // Convert FileList to array

    if (files.length > 0) {
      setDroppedFiles(files);
      console.log("Dropped Files:", files);
    } else {
      console.log("No files detected");
    }
  };

  return (
    <div className='admin-container d-flex-full d-flex-col'>
      {/* Drag and Drop Container */}
      <div
        className='drag-container'
        onDragOver={(e) => e.preventDefault()} // Allow drop
        onDrop={handleDrop}
        style={{
          width: "500px",
          height: "500px",
          backgroundColor: "#000",
          color: "#fff",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          border: "2px dashed white",
        }}
      >
        Drag & Drop Files Here
      </div>

      {/* Display uploaded file names */}
      {droppedFiles.length > 0 && (
        <ul style={{ color: "#fff", marginTop: "20px" }}>
          {droppedFiles.map((file, index) => (
            <li key={index}>{file.name}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Admin;
