import React, { useCallback, useState } from "react";
import { useDropzone } from "react-dropzone";
import "../styles/components/admin.scss";

const Admin = () => {
  // If you only expect a single file, initialize as null
  const [file, setFile] = useState(null);

  const onDrop = useCallback((acceptedFiles) => {
    // acceptedFiles is an array of File objects.
    // If you only need the first one, set it in state.
    if (acceptedFiles.length > 0) {
      setFile(acceptedFiles[0]);
      console.log("Dropped File:", acceptedFiles[0]);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
  });

  return (
    <div className='admin-container d-flex-full d-flex-col'>
      <div className='admin-inner-container d-flex-col gap-16 d-flex-jsc'>
        <div
          {...getRootProps()}
          style={{
            width: "500px",
            height: "500px",
            backgroundColor: isDragActive ? "#444" : "#000",
            color: "#fff",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            border: "2px dashed white",
            cursor: "pointer",
          }}
        >
          <input {...getInputProps()} />
          <p>
            {isDragActive
              ? "Drop the file here..."
              : "Drag & Drop a file here or click to upload"}
          </p>
        </div>
        {/* Display file information if available */}
        {file && (
          <div
            className='file-info'
            style={{ marginTop: "20px", color: "#fff" }}
          >
            <p>
              <strong>File Name:</strong> {file.name}
            </p>
            <p>
              <strong>File Type:</strong> {file.type}
            </p>
            <p>
              <strong>File Size:</strong> {file.size} bytes
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Admin;
